import { useState, useEffect } from "react";
import Login from "./Login";
import Results from "./Results";
import { vote, logout } from "./services";

function App() {
    const [user, setUser] = useState(null);
    const [message, setMessage] = useState("");

    // Check for existing session on mount
    useEffect(() => {
        const sessionToken = localStorage.getItem('sessionToken');
        const userData = localStorage.getItem('userData');

        if (sessionToken && userData) {
            try {
                setUser(JSON.parse(userData));
            } catch (e) {
                localStorage.clear();
            }
        }
    }, []);

    const castVote = async (candidate) => {
        const res = await vote(user.voterId, candidate);

        if (res.success) {
            setMessage(`‚úì ${res.message}`);
            // Update user's voted status
            const updatedUser = { ...user, hasVoted: true, canVote: false };
            setUser(updatedUser);
            localStorage.setItem('userData', JSON.stringify(updatedUser));
        } else {
            setMessage(`‚úó ${res.message}`);

            // If session expired, logout
            if (res.errorCode === "INVALID_SESSION") {
                setTimeout(() => handleLogout(), 2000);
            }
        }
    };

    const handleLogout = async () => {
        await logout();
        setUser(null);
        setMessage("");
    };

    if (!user) return <Login setUser={setUser} />;

    return (
        <div className="voting-container">
            <div className="header-section">
                <div className="welcome-message">
                    <div>
                        <h2>Welcome, {user.name || user.voterId}!</h2>
                    </div>
                    <div className="user-info">
                        <span className="user-badge">
                            {user.role === 'admin' ? 'üëë Admin' : 'üó≥Ô∏è Voter'}
                        </span>
                        <button onClick={handleLogout} className="logout-button">
                            Logout
                        </button>
                    </div>
                </div>
            </div>

            {user.role === 'voter' && (
                <div className="voting-section">
                    <h3>Cast Your Vote</h3>

                    {message && (
                        <div className={message.startsWith('‚úì') ? 'success-message' : 'error-message'}>
                            {message}
                        </div>
                    )}

                    {user.canVote ? (
                        <div className="candidates-grid">
                            <button className="candidate-card" onClick={() => castVote("Party A")}>
                                üéØ Party A
                            </button>
                            <button className="candidate-card" onClick={() => castVote("Party B")}>
                                üéØ Party B
                            </button>
                        </div>
                    ) : (
                        <div className="voted-message">
                            ‚úì You have already cast your vote. Thank you for participating!
                        </div>
                    )}
                </div>
            )}

            <Results />
        </div>
    );
}

export default App;
