import { useState } from "react";
import { requestOTP, verifyOTPLogin } from "./services";

export default function Login({ setUser }) {
    const [step, setStep] = useState(1); // 1: role + credentials, 2: OTP verification
    const [role, setRole] = useState("voter"); // voter or election_officer
    const [voterId, setVoterId] = useState("");
    const [password, setPassword] = useState("");
    const [email, setEmail] = useState("");
    const [otp, setOtp] = useState("");
    const [error, setError] = useState("");
    const [success, setSuccess] = useState("");
    const [loading, setLoading] = useState(false);

    const handleRequestOTP = async (e) => {
        e.preventDefault();
        setError("");
        setSuccess("");

        if (!voterId.trim()) {
            setError("Please enter your Voter ID");
            return;
        }

        if (!password.trim()) {
            setError("Please enter your password");
            return;
        }

        if (!email.trim()) {
            setError("Please enter your email address");
            return;
        }

        // Validate email
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(email)) {
            setError("Please enter a valid email address");
            return;
        }

        setLoading(true);

        try {
            const res = await requestOTP(voterId.trim(), password.trim(), email.trim(), role);

            if (res.success) {
                setSuccess("OTP sent to your email!");
                setStep(2);
            } else {
                setError(res.message || "Failed to send OTP. Please try again.");
            }
        } catch (err) {
            setError("Network error. Please check your connection and try again.");
        } finally {
            setLoading(false);
        }
    };

    const handleVerifyOTP = async (e) => {
        e.preventDefault();
        setError("");

        if (!otp.trim() || otp.length !== 6) {
            setError("Please enter the 6-digit OTP");
            return;
        }

        setLoading(true);

        try {
            const res = await verifyOTPLogin(voterId.trim(), email.trim(), otp.trim());

            if (res.success) {
                localStorage.setItem('sessionToken', res.sessionToken);
                localStorage.setItem('sessionSignature', res.sessionSignature);
                localStorage.setItem('userData', JSON.stringify(res.user));
                setUser(res.user);
            } else {
                setError(res.message || "Invalid OTP. Please try again.");
            }
        } catch (err) {
            setError("Network error. Please check your connection and try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="modern-login-container">
            <div className="modern-login-card">
                {/* Header */}
                <div className="modern-header">
                    <div className="logo-container">
                        <div className="logo-icon">üó≥Ô∏è</div>
                        <h1 className="logo-title">Smart Voting</h1>
                    </div>
                    <p className="subtitle">Secure Digital Democracy</p>
                </div>

                {step === 1 ? (
                    // Step 1: Role Selection + Credentials
                    <form onSubmit={handleRequestOTP} className="modern-form">
                        {error && <div className="modern-error">{error}</div>}
                        {success && <div className="modern-success">{success}</div>}

                        {/* Role Selection */}
                        <div className="form-field">
                            <label>Login As</label>
                            <div className="role-selector">
                                <button
                                    type="button"
                                    className={`role-btn ${role === 'voter' ? 'active' : ''}`}
                                    onClick={() => setRole('voter')}
                                    disabled={loading}
                                >
                                    üó≥Ô∏è Voter
                                </button>
                                <button
                                    type="button"
                                    className={`role-btn ${role === 'election_officer' ? 'active' : ''}`}
                                    onClick={() => setRole('election_officer')}
                                    disabled={loading}
                                >
                                    üëî Election Officer
                                </button>
                            </div>
                        </div>

                        {/* Voter ID */}
                        <div className="form-field">
                            <label>Voter ID</label>
                            <div className="input-container">
                                <span className="input-prefix">üÜî</span>
                                <input
                                    type="text"
                                    value={voterId}
                                    onChange={e => setVoterId(e.target.value)}
                                    placeholder="Enter your Voter ID"
                                    disabled={loading}
                                    className="modern-input"
                                />
                            </div>
                        </div>

                        {/* Password */}
                        <div className="form-field">
                            <label>Password</label>
                            <div className="input-container">
                                <span className="input-prefix">üîí</span>
                                <input
                                    type="password"
                                    value={password}
                                    onChange={e => setPassword(e.target.value)}
                                    placeholder="Enter your password"
                                    disabled={loading}
                                    className="modern-input"
                                />
                            </div>
                        </div>

                        {/* Email */}
                        <div className="form-field">
                            <label>Email Address</label>
                            <div className="input-container">
                                <span className="input-prefix">üìß</span>
                                <input
                                    type="email"
                                    value={email}
                                    onChange={e => setEmail(e.target.value)}
                                    placeholder="your@email.com"
                                    disabled={loading}
                                    className="modern-input"
                                />
                            </div>
                        </div>

                        <button type="submit" className="modern-submit" disabled={loading}>
                            {loading ? (
                                <span className="loading-spinner"></span>
                            ) : (
                                <>Send OTP</>
                            )}
                        </button>
                    </form>
                ) : (
                    // Step 2: Verify OTP
                    <form onSubmit={handleVerifyOTP} className="modern-form">
                        {error && <div className="modern-error">{error}</div>}

                        <div className="otp-info">
                            <p>Enter the 6-digit code sent to:</p>
                            <p className="contact-display">{email}</p>
                        </div>

                        <div className="form-field">
                            <label>One-Time Password</label>
                            <div className="otp-input-container">
                                <input
                                    type="text"
                                    value={otp}
                                    onChange={e => setOtp(e.target.value.replace(/\D/g, '').slice(0, 6))}
                                    placeholder="000000"
                                    disabled={loading}
                                    className="otp-input"
                                    maxLength="6"
                                    autoFocus
                                />
                            </div>
                        </div>

                        <button type="submit" className="modern-submit" disabled={loading}>
                            {loading ? (
                                <span className="loading-spinner"></span>
                            ) : (
                                <>Verify & Login</>
                            )}
                        </button>

                        <button
                            type="button"
                            className="back-button"
                            onClick={() => {
                                setStep(1);
                                setOtp("");
                                setError("");
                            }}
                            disabled={loading}
                        >
                            ‚Üê Back
                        </button>
                    </form>
                )}

                {/* Footer */}
                <div className="modern-footer">
                    <p className="footer-text">
                        üîí Secured with Password + OTP verification
                    </p>
                </div>
            </div>
        </div>
    );
}
