import { useState } from "react";
import { requestOTP, verifyOTPLogin } from "./services";

export default function Login({ setUser }) {
    const [step, setStep] = useState(1); // 1: enter contact, 2: enter OTP
    const [contactMethod, setContactMethod] = useState("email"); // email or phone
    const [contact, setContact] = useState("");
    const [otp, setOtp] = useState("");
    const [voterId, setVoterId] = useState("");
    const [error, setError] = useState("");
    const [success, setSuccess] = useState("");
    const [loading, setLoading] = useState(false);

    const handleRequestOTP = async (e) => {
        e.preventDefault();
        setError("");
        setSuccess("");

        if (!voterId.trim()) {
            setError("Please enter your Voter ID");
            return;
        }

        if (!contact.trim()) {
            setError(`Please enter your ${contactMethod === 'email' ? 'email address' : 'phone number'}`);
            return;
        }

        // Validate email or phone
        if (contactMethod === 'email') {
            const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
            if (!emailRegex.test(contact)) {
                setError("Please enter a valid email address");
                return;
            }
        } else {
            const phoneRegex = /^\d{10}$/;
            if (!phoneRegex.test(contact.replace(/\D/g, ''))) {
                setError("Please enter a valid 10-digit phone number");
                return;
            }
        }

        setLoading(true);

        try {
            const res = await requestOTP(voterId.trim(), contactMethod, contact.trim());

            if (res.success) {
                setSuccess(`OTP sent to your ${contactMethod}!`);
                setStep(2);
            } else {
                setError(res.message || "Failed to send OTP. Please try again.");
            }
        } catch (err) {
            setError("Network error. Please check your connection and try again.");
        } finally {
            setLoading(false);
        }
    };

    const handleVerifyOTP = async (e) => {
        e.preventDefault();
        setError("");

        if (!otp.trim() || otp.length !== 6) {
            setError("Please enter the 6-digit OTP");
            return;
        }

        setLoading(true);

        try {
            const res = await verifyOTPLogin(voterId.trim(), contactMethod, contact.trim(), otp.trim());

            if (res.success) {
                localStorage.setItem('sessionToken', res.sessionToken);
                localStorage.setItem('sessionSignature', res.sessionSignature);
                localStorage.setItem('userData', JSON.stringify(res.user));
                setUser(res.user);
            } else {
                setError(res.message || "Invalid OTP. Please try again.");
            }
        } catch (err) {
            setError("Network error. Please check your connection and try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="modern-login-container">
            <div className="modern-login-card">
                {/* Header */}
                <div className="modern-header">
                    <div className="logo-container">
                        <div className="logo-icon">üó≥Ô∏è</div>
                        <h1 className="logo-title">Smart Voting</h1>
                    </div>
                    <p className="subtitle">Secure Digital Democracy</p>
                </div>

                {step === 1 ? (
                    // Step 1: Request OTP
                    <form onSubmit={handleRequestOTP} className="modern-form">
                        {error && <div className="modern-error">{error}</div>}
                        {success && <div className="modern-success">{success}</div>}

                        <div className="form-field">
                            <label>Voter ID</label>
                            <div className="input-container">
                                <span className="input-prefix">üÜî</span>
                                <input
                                    type="text"
                                    value={voterId}
                                    onChange={e => setVoterId(e.target.value)}
                                    placeholder="Enter your Voter ID"
                                    disabled={loading}
                                    className="modern-input"
                                />
                            </div>
                        </div>

                        <div className="form-field">
                            <label>Verification Method</label>
                            <div className="method-selector">
                                <button
                                    type="button"
                                    className={`method-btn ${contactMethod === 'email' ? 'active' : ''}`}
                                    onClick={() => setContactMethod('email')}
                                    disabled={loading}
                                >
                                    üìß Email
                                </button>
                                <button
                                    type="button"
                                    className={`method-btn ${contactMethod === 'phone' ? 'active' : ''}`}
                                    onClick={() => setContactMethod('phone')}
                                    disabled={loading}
                                >
                                    üì± Phone
                                </button>
                            </div>
                        </div>

                        <div className="form-field">
                            <label>{contactMethod === 'email' ? 'Email Address' : 'Phone Number'}</label>
                            <div className="input-container">
                                <span className="input-prefix">{contactMethod === 'email' ? 'üìß' : 'üì±'}</span>
                                <input
                                    type={contactMethod === 'email' ? 'email' : 'tel'}
                                    value={contact}
                                    onChange={e => setContact(e.target.value)}
                                    placeholder={contactMethod === 'email' ? 'your@email.com' : '1234567890'}
                                    disabled={loading}
                                    className="modern-input"
                                />
                            </div>
                        </div>

                        <button type="submit" className="modern-submit" disabled={loading}>
                            {loading ? (
                                <span className="loading-spinner"></span>
                            ) : (
                                <>Send OTP</>
                            )}
                        </button>
                    </form>
                ) : (
                    // Step 2: Verify OTP
                    <form onSubmit={handleVerifyOTP} className="modern-form">
                        {error && <div className="modern-error">{error}</div>}

                        <div className="otp-info">
                            <p>Enter the 6-digit code sent to:</p>
                            <p className="contact-display">{contact}</p>
                        </div>

                        <div className="form-field">
                            <label>One-Time Password</label>
                            <div className="otp-input-container">
                                <input
                                    type="text"
                                    value={otp}
                                    onChange={e => setOtp(e.target.value.replace(/\D/g, '').slice(0, 6))}
                                    placeholder="000000"
                                    disabled={loading}
                                    className="otp-input"
                                    maxLength="6"
                                    autoFocus
                                />
                            </div>
                        </div>

                        <button type="submit" className="modern-submit" disabled={loading}>
                            {loading ? (
                                <span className="loading-spinner"></span>
                            ) : (
                                <>Verify & Login</>
                            )}
                        </button>

                        <button
                            type="button"
                            className="back-button"
                            onClick={() => {
                                setStep(1);
                                setOtp("");
                                setError("");
                            }}
                            disabled={loading}
                        >
                            ‚Üê Back
                        </button>
                    </form>
                )}

                {/* Footer */}
                <div className="modern-footer">
                    <p className="footer-text">
                        üîí Secured with OTP verification
                    </p>
                </div>
            </div>
        </div>
    );
}
