import { useState } from "react";
import Login from "./Login";
import Results from "./Results";
import { vote } from "./services";

function App() {
    const [user, setUser] = useState(null);

    const castVote = async (candidate) => {
        const res = await vote(user.voterId, candidate);
        alert(res.message);
    };

    if (!user) return <Login setUser={setUser} />;

    return (
        <div>
            <h2>Welcome {user.voterId}</h2>

            {!user.hasVoted && (
                <>
                    <button onClick={() => castVote("Party A")}>Party A</button>
                    <button onClick={() => castVote("Party B")}>Party B</button>
                </>
            )}

            <Results />
        </div>
    );
}

export default App;
