import { useState } from "react";
import { login } from "./services";

export default function Login({ setUser }) {
    const [voterId, setVoterId] = useState("");
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);

    const submit = async (e) => {
        e.preventDefault();

        // Clear previous errors
        setError("");

        // Validate input
        if (!voterId.trim()) {
            setError("Please enter your Voter ID");
            return;
        }

        setLoading(true);

        try {
            const res = await login(voterId.trim());

            if (res.success) {
                // Store session in local storage
                localStorage.setItem('sessionToken', res.sessionToken);
                localStorage.setItem('userData', JSON.stringify(res.user));
                setUser(res.user);
            } else {
                setError(res.message || "Login failed. Please try again.");
            }
        } catch (err) {
            setError("Network error. Please check your connection and try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="login-container">
            <div className="login-card">
                <div className="login-header">
                    <h1>Smart Voting System</h1>
                    <p>Secure Voter Authentication Portal</p>
                    <div className="security-badge">
                        Secured with Session-Based Authentication
                    </div>
                </div>

                <form onSubmit={submit}>
                    {error && (
                        <div className="error-message">
                            {error}
                        </div>
                    )}

                    <div className="form-group">
                        <label htmlFor="voterId">Voter ID</label>
                        <div className="input-wrapper">
                            <input
                                id="voterId"
                                type="text"
                                value={voterId}
                                onChange={e => setVoterId(e.target.value)}
                                placeholder="Enter your Voter ID"
                                disabled={loading}
                                autoComplete="off"
                                autoFocus
                            />
                            <span className="input-icon">üë§</span>
                        </div>
                    </div>

                    <button
                        type="submit"
                        className={`login-button ${loading ? 'loading' : ''}`}
                        disabled={loading}
                    >
                        {loading ? "" : "Login Securely"}
                    </button>
                </form>

                <div className="info-box">
                    <h4>üîê Security Features</h4>
                    <p>
                        ‚Ä¢ Voter ID-based authentication<br />
                        ‚Ä¢ Role-based access control<br />
                        ‚Ä¢ Duplicate vote prevention<br />
                        ‚Ä¢ Secure session management
                    </p>
                </div>

                <div className="test-credentials">
                    <strong>Test Voter IDs:</strong>
                    <code>V1001</code> <code>V1002</code> <code>V1003</code> or <code>ADMIN001</code>
                </div>
            </div>
        </div>
    );
}
