import { useState } from "react";
import { login } from "./services";

export default function Login({ setUser }) {
    const [voterId, setVoterId] = useState("");
    const [password, setPassword] = useState("");
    const [mfaToken, setMfaToken] = useState("");
    const [showPassword, setShowPassword] = useState(false);
    const [needsMFA, setNeedsMFA] = useState(false);
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);

    const submit = async (e) => {
        e.preventDefault();

        // Clear previous errors
        setError("");

        // Validate input
        if (!voterId.trim()) {
            setError("Please enter your Voter ID");
            return;
        }

        if (!password.trim()) {
            setError("Please enter your password");
            return;
        }

        if (needsMFA && !mfaToken.trim()) {
            setError("Please enter your 6-digit authentication code");
            return;
        }

        setLoading(true);

        try {
            const res = await login(voterId.trim(), password.trim(), needsMFA ? mfaToken.trim() : null);

            if (res.success) {
                // Store session in local storage
                localStorage.setItem('sessionToken', res.sessionToken);
                localStorage.setItem('sessionSignature', res.sessionSignature);
                localStorage.setItem('userData', JSON.stringify(res.user));
                setUser(res.user);
            } else {
                // Check if MFA is required
                if (res.errorCode === "MFA_TOKEN_REQUIRED") {
                    setNeedsMFA(true);
                    setError("Please enter your authentication code");
                } else if (res.errorCode === "MFA_SETUP_REQUIRED") {
                    setError(res.message || "MFA setup is required for your account");
                } else {
                    setError(res.message || "Login failed. Please try again.");
                }
            }
        } catch (err) {
            setError("Network error. Please check your connection and try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="login-container">
            <div className="login-card">
                <div className="login-header">
                    <h1>üó≥Ô∏è Smart Voting System</h1>
                    <p>Secure Voter Authentication Portal</p>
                    <div className="security-badge">
                        üîí NIST SP 800-63-2 Compliant
                    </div>
                </div>

                <form onSubmit={submit}>
                    {error && (
                        <div className="error-message">
                            ‚ö†Ô∏è {error}
                        </div>
                    )}

                    <div className="form-group">
                        <label htmlFor="voterId">Voter ID</label>
                        <div className="input-wrapper">
                            <input
                                id="voterId"
                                type="text"
                                value={voterId}
                                onChange={e => setVoterId(e.target.value)}
                                placeholder="Enter your Voter ID"
                                disabled={loading}
                                autoComplete="username"
                                autoFocus
                            />
                            <span className="input-icon">üë§</span>
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <div className="input-wrapper">
                            <input
                                id="password"
                                type={showPassword ? "text" : "password"}
                                value={password}
                                onChange={e => setPassword(e.target.value)}
                                placeholder="Enter your password"
                                disabled={loading}
                                autoComplete="current-password"
                            />
                            <button
                                type="button"
                                className="password-toggle"
                                onClick={() => setShowPassword(!showPassword)}
                                tabIndex="-1"
                            >
                                {showPassword ? 'üôà' : 'üëÅÔ∏è'}
                            </button>
                        </div>
                    </div>

                    {needsMFA && (
                        <div className="form-group mfa-group">
                            <label htmlFor="mfaToken">Authentication Code</label>
                            <div className="input-wrapper">
                                <input
                                    id="mfaToken"
                                    type="text"
                                    value={mfaToken}
                                    onChange={e => setMfaToken(e.target.value.replace(/\D/g, '').slice(0, 6))}
                                    placeholder="000000"
                                    disabled={loading}
                                    maxLength="6"
                                    autoComplete="one-time-code"
                                    className="mfa-input"
                                />
                                <span className="input-icon">üîê</span>
                            </div>
                            <small className="help-text">
                                Enter the 6-digit code from your authenticator app
                            </small>
                        </div>
                    )}

                    <button
                        type="submit"
                        className={`login-button ${loading ? 'loading' : ''}`}
                        disabled={loading}
                    >
                        {loading ? "Authenticating..." : "Login Securely"}
                    </button>
                </form>

                <div className="info-box">
                    <h4>üîê Security Features</h4>
                    <p>
                        ‚Ä¢ Password-based authentication with salted hashing<br />
                        ‚Ä¢ Optional multi-factor authentication (2FA)<br />
                        ‚Ä¢ Role-based access control<br />
                        ‚Ä¢ Brute-force protection & account lockout<br />
                        ‚Ä¢ Session management with integrity verification<br />
                        ‚Ä¢ Comprehensive audit logging
                    </p>
                </div>

                <div className="test-credentials">
                    <strong>Test Credentials (Development Only):</strong><br />
                    <code>V1001</code> / <code>password123</code> (Voter)<br />
                    <code>ADMIN001</code> / <code>adminpass123</code> (Admin - MFA required)<br />
                    <code>OFFICER001</code> / <code>officer123</code> (Officer - MFA required)
                </div>
            </div>
        </div>
    );
}
